package acadgild.assignments;

public class session1assign4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=5,y=10;
		int value;
		
		value= x+y*2;
		System.out.println("The value of x+y*2 is : "+value);
		
		value=x-y+2;
		System.out.println("The value of x-y+2 is : "+value);
		
		value=(x+y)*2;
		System.out.println("The value of (x+y)*2 is : "+value);
		
		value= y%x;
		System.out.println("The value of y%x is : "+value);



	}

}
